import React, { useState, useEffect } from 'react';
import './LandingPage.css';

const LandingPage = () => {
    const [searchInput, setSearchInput] = useState('');
    const [showLocationOptions, setShowLocationOptions] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [restaurants, setRestaurants] = useState([]);
    const [googleMapsLoaded, setGoogleMapsLoaded] = useState(false);

    useEffect(() => {
        const loadGoogleMaps = () => {
            // Remove any existing Google Maps scripts first
            const existingScript = document.querySelector('script[src*="maps.googleapis.com/maps/api"]');
            if (existingScript) {
                existingScript.remove();
            }

            if (window.google) {
                setGoogleMapsLoaded(true);
                return;
            }

            const script = document.createElement('script');
            script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&libraries=places&loading=async`;
            script.async = true;
            script.defer = true;

            script.onload = () => {
                setGoogleMapsLoaded(true);
                console.log('Google Maps loaded successfully');
            };

            script.onerror = () => {
                setError('Failed to load Google Maps');
                console.error('Google Maps failed to load');
            };

            document.head.appendChild(script);
        };

        loadGoogleMaps();

        return () => {
            // Cleanup on component unmount
            const script = document.querySelector('script[src*="maps.googleapis.com/maps/api"]');
            if (script) {
                script.remove();
            }
        };
    }, []);

    // Initialize autocomplete when Google Maps is loaded
    useEffect(() => {
        if (googleMapsLoaded) {
            const input = document.getElementById('location-search');
            if (input) {
                const autocomplete = new window.google.maps.places.Autocomplete(input, {
                    types: ['(cities)'],
                    componentRestrictions: { country: "us" }
                });

                autocomplete.addListener('place_changed', () => {
                    const place = autocomplete.getPlace();
                    if (place.geometry) {
                        setSearchInput(place.formatted_address);
                        setShowLocationOptions(false);
                        searchNearbyRestaurants(
                            place.geometry.location.lat(),
                            place.geometry.location.lng()
                        );
                    }
                });
            }
        }
    }, [googleMapsLoaded]);

    const searchNearbyRestaurants = async (latitude, longitude) => {
        if (!googleMapsLoaded || !window.google) {
            setError('Google Maps is not loaded yet');
            return;
        }

        try {
            setLoading(true);
            setError(null);

            // Create a persistent map element
            const mapDiv = document.createElement('div');
            mapDiv.style.display = 'none';
            document.body.appendChild(mapDiv);

            const location = { lat: parseFloat(latitude), lng: parseFloat(longitude) };

            const map = new window.google.maps.Map(mapDiv, {
                center: location,
                zoom: 15
            });

            const service = new window.google.maps.places.PlacesService(map);

            const results = await new Promise((resolve, reject) => {
                service.nearbySearch({
                    location,
                    radius: 5000,
                    type: ['restaurant']
                }, (results, status) => {
                    // Always remove the map div after the search
                    document.body.removeChild(mapDiv);

                    if (status === window.google.maps.places.PlacesServiceStatus.OK && results) {
                        resolve(results);
                    } else {
                        reject(new Error(`No restaurants found: ${status}`));
                    }
                });
            });

            setRestaurants(results);
        } catch (err) {
            setError(`Error searching restaurants: ${err.message}`);
            console.error('Error searching restaurants:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleCurrentLocation = () => {
        if (!navigator.geolocation) {
            setError('Geolocation is not supported by your browser');
            return;
        }

        setLoading(true);
        setError(null);

        navigator.geolocation.getCurrentPosition(
            async (position) => {
                try {
                    const { latitude, longitude } = position.coords;

                    if (googleMapsLoaded) {
                        const geocoder = new window.google.maps.Geocoder();
                        const response = await new Promise((resolve, reject) => {
                            geocoder.geocode(
                                { location: { lat: latitude, lng: longitude } },
                                (results, status) => {
                                    if (status === 'OK' && results[0]) {
                                        resolve(results[0]);
                                    } else {
                                        reject(new Error('Could not find address'));
                                    }
                                }
                            );
                        });

                        setSearchInput(response.formatted_address);
                        await searchNearbyRestaurants(latitude, longitude);
                    }
                } catch (err) {
                    setError('Error getting location details: ' + err.message);
                } finally {
                    setLoading(false);
                }
            },
            (error) => {
                setError('Error accessing your location: ' + error.message);
                setLoading(false);
            }
        );
    };

    const handleFocus = () => setShowLocationOptions(true);
    const handleBlur = () => {
        setTimeout(() => setShowLocationOptions(false), 200);
    };


    return (
        <div className="landing-page">
            <div className="hero-section">
                <h1>Find your next favorite restaurant</h1>
                <div className="main-search">
                    <div className="search-container">
                        <input
                            id="location-search"
                            type="text"
                            placeholder="Search restaurants near you..."
                            className="main-search-input"
                            value={searchInput}
                            onChange={(e) => setSearchInput(e.target.value)}
                            onFocus={handleFocus}
                            onBlur={handleBlur}
                            disabled={loading || !googleMapsLoaded}
                        />
                        <button
                            className="main-search-button"
                            disabled={loading || !googleMapsLoaded}
                        >
                            <i className="fas fa-search"></i>
                        </button>
                    </div>

                    {showLocationOptions && (
                        <div className="location-options">
                            <button
                                className="use-location-btn"
                                onClick={handleCurrentLocation}
                                disabled={loading || !googleMapsLoaded}
                            >
                                <i className="fas fa-location-arrow"></i>
                                {loading ? 'Getting location...' : 'Use current location'}
                            </button>
                        </div>
                    )}

                    {error && (
                        <div className="error-message">
                            {error}
                            <button
                                className="close-error"
                                onClick={() => setError(null)}
                            >
                                <i className="fas fa-times"></i>
                            </button>
                        </div>
                    )}
                </div>
            </div>

            <div className="categories-section">
                <h2>Popular Categories</h2>
                <div className="category-grid">
                    <div className="category-card">
                        <img
                            src="https://images.unsplash.com/photo-1514326640560-7d063ef2aed5?w=400&h=300&q=80"
                            alt="Fine Dining"
                        />
                        <h3>Fine Dining</h3>
                    </div>
                    <div className="category-card">
                        <img
                            src="https://images.unsplash.com/photo-1565299624946-b28f40a0ae38?w=400&h=300&q=80"
                            alt="Casual Dining"
                        />
                        <h3>Casual Dining</h3>
                    </div>
                    <div className="category-card">
                        <img
                            src="https://images.unsplash.com/photo-1513185158878-8d8c2a2a3da3?w=400&h=300&q=80"
                            alt="Fast Food"
                        />
                        <h3>Fast Food</h3>
                    </div>
                </div>
            </div>

            {restaurants.length > 0 && (
                <div className="restaurants-section">
                    <h2>Restaurants Near You</h2>
                    <div className="restaurants-grid">
                        {restaurants.map((restaurant) => (
                            <div key={restaurant.place_id} className="restaurant-card">
                                <h3>{restaurant.name}</h3>
                                <p>{restaurant.vicinity}</p>
                                <p>Rating: {restaurant.rating} ‚≠ê</p>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default LandingPage;
