import React, { useState, useEffect, useRef } from 'react';
import './LandingPage.css';

const LandingPage = () => {
    const [searchInput, setSearchInput] = useState('');
    const [showLocationOptions, setShowLocationOptions] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [restaurants, setRestaurants] = useState([]);
    const [googleMapsLoaded, setGoogleMapsLoaded] = useState(false);
    const [map, setMap] = useState(null);
    const [markers, setMarkers] = useState([]);
    const mapRef = useRef(null);
    const mapContainerRef = useRef(null);
    const [selectedPlace, setSelectedPlace] = useState(null);

    useEffect(() => {
        const loadGoogleMaps = () => {
            if (window.google) {
                window.google = undefined;
            }

            const existingScripts = document.querySelectorAll('script[src*="maps.googleapis.com"]');
            existingScripts.forEach(script => script.remove());

            return new Promise((resolve, reject) => {
                const script = document.createElement('script');
                script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&libraries=places`;
                script.async = true;
                script.defer = true;

                script.addEventListener('load', () => {
                    if (window.google && window.google.maps) {
                        setGoogleMapsLoaded(true);
                        initializeMap();
                        console.log('Google Maps loaded successfully');
                        resolve();
                    } else {
                        reject(new Error('Google Maps failed to load correctly'));
                    }
                });

                script.addEventListener('error', () => {
                    reject(new Error('Failed to load Google Maps'));
                });

                document.head.appendChild(script);
            });
        };

        loadGoogleMaps().catch(error => {
            console.error('Error loading Google Maps:', error);
            setError(error.message);
        });

        return () => {
            const script = document.querySelector('script[src*="maps.googleapis.com"]');
            if (script) {
                script.remove();
            }
        };
    }, []);

    const initializeMap = () => {
        if (!mapContainerRef.current) return;

        const newMap = new window.google.maps.Map(mapContainerRef.current, {
            center: { lat: 40.7128, lng: -74.0060 }, // Default to NYC
            zoom: 13,
            styles: [
                {
                    featureType: "poi",
                    elementType: "labels",
                    stylers: [{ visibility: "off" }]
                }
            ]
        });

        setMap(newMap);
        mapRef.current = newMap;
    };

    useEffect(() => {
        let autocomplete = null;

        const initializeAutocomplete = () => {
            if (!window.google || !window.google.maps || !window.google.maps.places) {
                console.error('Google Maps Places API not loaded');
                return;
            }

            const input = document.getElementById('location-search');
            if (!input) {
                console.error('Search input not found');
                return;
            }

            try {
                autocomplete = new window.google.maps.places.Autocomplete(input, {
                    types: ['address', 'establishment', 'geocode'],
                    componentRestrictions: { country: "us" }
                });

                autocomplete.addListener('place_changed', async () => {
                    const place = autocomplete.getPlace();
                    if (place.geometry && place.geometry.location) {
                        setSearchInput(place.formatted_address);
                        setShowLocationOptions(false);
                        setSelectedPlace(place);

                        const lat = place.geometry.location.lat();
                        const lng = place.geometry.location.lng();

                        if (mapRef.current) {
                            mapRef.current.setCenter({ lat, lng });
                            mapRef.current.setZoom(15);
                        }

                        await searchNearbyRestaurants(lat, lng);
                    } else {
                        setError('No details available for this location');
                    }
                });
            } catch (error) {
                console.error('Error initializing autocomplete:', error);
                setError('Error initializing location search');
            }
        };

        if (googleMapsLoaded) {
            initializeAutocomplete();
        }

        return () => {
            if (autocomplete) {
                window.google.maps.event.clearInstanceListeners(autocomplete);
            }
        };
    }, [googleMapsLoaded]);

    const clearMarkers = () => {
        markers.forEach(marker => marker.setMap(null));
        setMarkers([]);
    };

    const searchNearbyRestaurants = async (latitude, longitude) => {
        if (!window.google || !window.google.maps || !window.google.maps.places) {
            setError('Google Maps Places API is not loaded');
            return;
        }

        try {
            setLoading(true);
            setError(null);
            clearMarkers();

            const location = {
                lat: typeof latitude === 'function' ? latitude() : parseFloat(latitude),
                lng: typeof longitude === 'function' ? longitude() : parseFloat(longitude)
            };

            if (!mapRef.current) {
                console.error('Map not initialized');
                return;
            }

            const service = new window.google.maps.places.PlacesService(mapRef.current);

            const results = await new Promise((resolve, reject) => {
                service.nearbySearch({
                    location: location,
                    radius: 5000,
                    type: ['restaurant']
                }, (results, status) => {
                    if (status === window.google.maps.places.PlacesServiceStatus.OK) {
                        resolve(results || []);
                    } else {
                        reject(new Error(`Places search failed: ${status}`));
                    }
                });
            });

            const newMarkers = results.map(place => {
                const marker = new window.google.maps.Marker({
                    position: place.geometry.location,
                    map: mapRef.current,
                    title: place.name,
                    animation: window.google.maps.Animation.DROP
                });

                const infoWindow = new window.google.maps.InfoWindow({
                    content: `
                        <div style="padding: 10px;">
                            <h3 style="margin: 0 0 5px 0;">${place.name}</h3>
                            <p style="margin: 0 0 5px 0;">${place.vicinity}</p>
                            <p style="margin: 0;">Rating: ${place.rating || 'N/A'} ⭐</p>
                        </div>
                    `
                });

                marker.addListener('click', () => {
                    infoWindow.open(mapRef.current, marker);
                });

                return marker;
            });

            setMarkers(newMarkers);
            setRestaurants(results);
        } catch (err) {
            console.error('Error in searchNearbyRestaurants:', err);
            setError(`Error searching restaurants: ${err.message}`);
        } finally {
            setLoading(false);
        }
    };

    const handleCurrentLocation = () => {
        if (!navigator.geolocation) {
            setError('Geolocation is not supported by your browser');
            return;
        }

        setLoading(true);
        setError(null);

        navigator.geolocation.getCurrentPosition(
            async (position) => {
                try {
                    const { latitude, longitude } = position.coords;

                    if (googleMapsLoaded) {
                        const geocoder = new window.google.maps.Geocoder();
                        const response = await new Promise((resolve, reject) => {
                            geocoder.geocode(
                                { location: { lat: latitude, lng: longitude } },
                                (results, status) => {
                                    if (status === 'OK' && results[0]) {
                                        resolve(results[0]);
                                    } else {
                                        reject(new Error('Could not find address'));
                                    }
                                }
                            );
                        });

                        setSearchInput(response.formatted_address);
                        if (mapRef.current) {
                            mapRef.current.setCenter({ lat: latitude, lng: longitude });
                            mapRef.current.setZoom(15);
                        }
                        await searchNearbyRestaurants(latitude, longitude);
                    }
                } catch (err) {
                    setError('Error getting location details: ' + err.message);
                } finally {
                    setLoading(false);
                }
            },
            (error) => {
                setError('Error accessing your location: ' + error.message);
                setLoading(false);
            }
        );
    };

    const handleSearch = () => {
        if (!searchInput) return;

        if (window.google && window.google.maps) {
            const geocoder = new window.google.maps.Geocoder();

            geocoder.geocode({ address: searchInput }, (results, status) => {
                if (status === 'OK' && results[0]) {
                    const location = results[0].geometry.location;
                    if (mapRef.current) {
                        mapRef.current.setCenter(location);
                        mapRef.current.setZoom(15);
                    }
                    searchNearbyRestaurants(location.lat(), location.lng());
                } else {
                    setError('Could not find location. Please try again.');
                }
            });
        }
    };

    const handleFocus = () => setShowLocationOptions(true);
    const handleBlur = () => {
        setTimeout(() => setShowLocationOptions(false), 200);
    };

    return (
        <div className="landing-page">
            <div className="hero-section">
                <h1>Find your next favorite restaurant</h1>
                <div className="main-search">
                    <div className="search-container">
                        <input
                            id="location-search"
                            type="text"
                            placeholder="Enter any address..."
                            className="main-search-input"
                            value={searchInput}
                            onChange={(e) => setSearchInput(e.target.value)}
                            onFocus={handleFocus}
                            onBlur={handleBlur}
                            onKeyPress={(e) => {
                                if (e.key === 'Enter') {
                                    handleSearch();
                                }
                            }}
                            disabled={loading || !googleMapsLoaded}
                        />
                        <button
                            className="main-search-button"
                            disabled={loading || !googleMapsLoaded}
                            onClick={handleSearch}
                        >
                            <i className="fas fa-search"></i>
                        </button>
                    </div>

                    {showLocationOptions && (
                        <div className="location-options">
                            <button
                                className="use-location-btn"
                                onClick={handleCurrentLocation}
                                disabled={loading || !googleMapsLoaded}
                            >
                                <i className="fas fa-location-arrow"></i>
                                {loading ? 'Getting location...' : 'Use current location'}
                            </button>
                        </div>
                    )}

                    {error && (
                        <div className="error-message">
                            {error}
                            <button
                                className="close-error"
                                onClick={() => setError(null)}
                            >
                                <i className="fas fa-times"></i>
                            </button>
                        </div>
                    )}
                </div>
            </div>

            <div 
                ref={mapContainerRef}
                className="map-container"
            />

            {restaurants.length > 0 && (
                <div className="restaurants-section">
                    <h2>Restaurants Near You</h2>
                    <div className="restaurants-grid">
                        {restaurants.map((restaurant) => (
                            <div 
                                key={restaurant.place_id} 
                                className="restaurant-card"
                                onClick={() => {
                                    const marker = markers.find(m => m.getTitle() === restaurant.name);
                                    if (marker && mapRef.current) {
                                        window.google.maps.event.trigger(marker, 'click');
                                        mapRef.current.panTo(marker.getPosition());
                                        mapRef.current.setZoom(16);
                                    }
                                }}
                            >
                                <h3>{restaurant.name}</h3>
                                <p>{restaurant.vicinity}</p>
                                <p>Rating: {restaurant.rating} ⭐</p>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default LandingPage;
