import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './MenuPage.css';

const MenuPage = () => {
    const { placeId } = useParams();
    const navigate = useNavigate();
    const [menuData, setMenuData] = useState(null);
    const [restaurant, setRestaurant] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchMenu = async () => {
            try {
                const response = await fetch(`http://localhost:8000/api/restaurants/menu/${placeId}`);
                const data = await response.json();

                if (response.ok && data.menu) {
                    setMenuData(data.menu);
                    setRestaurant(data.restaurant);
                } else {
                    setError(data.message || 'Menu not available');
                }
            } catch (error) {
                console.error('Error fetching menu:', error);
                setError('Failed to load menu');
            } finally {
                setLoading(false);
            }
        };

        fetchMenu();
    }, [placeId]);

    return (
        <div className="menu-page">
            <div className="menu-header">
                <button onClick={() => navigate(-1)} className="back-button">
                    <i className="fas fa-arrow-left"></i> Back to Results
                </button>
                <h1>{restaurant?.name || 'Restaurant'} Menu</h1>
            </div>

            <div className="menu-content">
                {loading && <div className="loading">Loading menu...</div>}
                {error && <div className="error-message">{error}</div>}

                {menuData && (
                    <div className="menu-items">
                        {menuData.map((item, index) => (
                            <div key={index} className="menu-item">
                                {/* Static image for demonstration */}
                                <img
                                    src={`https://via.placeholder.com/150?text=${encodeURIComponent(item.name)}`}
                                    alt={item.name}
                                    className="menu-item-image"
                                />
                                <div className="item-info">
                                    <div className="item-details">
                                        <span className="item-name">{item.name}</span>
                                        <span className="item-price">${item.price}</span>
                                    </div>
                                    <div className="item-rating">
                                        {/* Ratings - Static for now */}
                                        {'⭐'.repeat(4)} <span className="rating-text">(4.0)</span>
                                    </div>
                                    <button
                                        className="reviews-dropdown-toggle"
                                        onClick={() =>
                                            document.getElementById(`reviews-${index}`).classList.toggle('hidden')
                                        }
                                    >
                                        Show Reviews
                                    </button>
                                    <div id={`reviews-${index}`} className="reviews-dropdown hidden">
                                        <p>Review 1: "Delicious!"</p>
                                        <p>Review 2: "Would try again."</p>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default MenuPage;
