import React, { useState, useEffect } from 'react';
import './LandingPage.css';

const LandingPage = () => {
    const [searchInput, setSearchInput] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [restaurants, setRestaurants] = useState([]);
    const [googleMapsLoaded, setGoogleMapsLoaded] = useState(false);

    useEffect(() => {
        // Load Google Maps only if it hasn't been loaded yet
        if (!window.google) {
            const script = document.createElement('script');
            script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&libraries=places`;
            script.async = true;
            script.defer = true;
            script.onload = () => {
                setGoogleMapsLoaded(true);
                initializeAutocomplete();
            };
            document.head.appendChild(script);
        } else {
            setGoogleMapsLoaded(true);
            initializeAutocomplete();
        }
    }, []);

    const initializeAutocomplete = () => {
        if (!window.google || !window.google.maps || !window.google.maps.places) return;

        const input = document.getElementById('location-search');
        if (!input) return;

        const autocomplete = new window.google.maps.places.Autocomplete(input, {
            componentRestrictions: { country: "us" }
        });

        autocomplete.addListener('place_changed', async () => {
            const place = autocomplete.getPlace();
            if (place.geometry && place.geometry.location) {
                setSearchInput(place.formatted_address);
                const lat = place.geometry.location.lat();
                const lng = place.geometry.location.lng();
                await searchNearbyRestaurants(lat, lng);
            }
        });
    };

    // Keep your existing searchNearbyRestaurants function and other code...

    return (
        <div className="landing-page">
            <div className="hero-section">
                <h1>Find your next favorite restaurant</h1>
                <div className="main-search">
                    <div className="search-container">
                        <input
                            id="location-search"
                            type="text"
                            placeholder="Enter any address..."
                            className="main-search-input"
                            value={searchInput}
                            onChange={(e) => setSearchInput(e.target.value)}
                            disabled={loading || !googleMapsLoaded}
                        />
                        <button
                            className="main-search-button"
                            disabled={loading || !googleMapsLoaded}
                            onClick={() => handleSearch()}
                        >
                            <i className="fas fa-search"></i>
                        </button>
                    </div>

                    {error && (
                        <div className="error-message">
                            {error}
                            <button
                                className="close-error"
                                onClick={() => setError(null)}
                            >
                                <i className="fas fa-times"></i>
                            </button>
                        </div>
                    )}
                </div>
            </div>

            {/* Rest of your component... */}
        </div>
    );
};

export default LandingPage;
