import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './RestaurantResults.css';

const RestaurantResults = () => {
    const [restaurants, setRestaurants] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [map, setMap] = useState(null);
    const location = useLocation();
    const navigate = useNavigate();
    const { searchLocation } = location.state || {};

    useEffect(() => {
        if (!searchLocation) {
            navigate('/');
            return;
        }

        const fetchRestaurantDetails = async (place) => {
            return new Promise((resolve, reject) => {
                const service = new window.google.maps.places.PlacesService(map);
                service.getDetails(
                    {
                        placeId: place.place_id,
                        fields: ['name', 'rating', 'formatted_address', 'photos', 'opening_hours', 'price_level']
                    },
                    (result, status) => {
                        if (status === window.google.maps.places.PlacesServiceStatus.OK) {
                            resolve(result);
                        } else {
                            resolve(place); // Fallback to original place data
                        }
                    }
                );
            });
        };

        const initMap = async () => {
            if (!window.google) {
                setError('Google Maps not loaded');
                return;
            }

            try {
                const mapInstance = new window.google.maps.Map(
                    document.getElementById('map'),
                    {
                        center: searchLocation.coordinates,
                        zoom: 14,
                        styles: [
                            {
                                featureType: "poi",
                                elementType: "labels",
                                stylers: [{ visibility: "off" }]
                            }
                        ]
                    }
                );

                setMap(mapInstance);

                const service = new window.google.maps.places.PlacesService(mapInstance);

                const results = await new Promise((resolve, reject) => {
                    service.nearbySearch({
                        location: searchLocation.coordinates,
                        radius: 5000,
                        type: ['restaurant']
                    }, (results, status) => {
                        if (status === window.google.maps.places.PlacesServiceStatus.OK) {
                            resolve(results);
                        } else {
                            reject(new Error('Failed to find restaurants'));
                        }
                    });
                });

                const handleSearch = () => {
    if (!searchInput) return;

    const geocoder = new window.google.maps.Geocoder();
    geocoder.geocode({ address: searchInput }, (results, status) => {
        if (status === 'OK' && results[0]) {
            const location = results[0].geometry.location;
            navigate('/restaurants', {
                state: {
                    searchLocation: {
                        address: searchInput,
                        coordinates: {
                            lat: location.lat(),
                            lng: location.lng()
                        }
                    }
                }
            });
        } else {
            setError('Could not find location');
        }
    });
};

                // Get detailed information for each restaurant
                const detailedResults = await Promise.all(
                    results.map(place => fetchRestaurantDetails(place))
                );

                // Add markers
                detailedResults.forEach(place => {
                    if (place.geometry) {
                        const marker = new window.google.maps.Marker({
                            position: place.geometry.location,
                            map: mapInstance,
                            title: place.name
                        });

                        const infoWindow = new window.google.maps.InfoWindow({
                            content: `
                                <div style="padding: 10px;">
                                    <h3 style="margin: 0 0 5px 0;">${place.name}</h3>
                                    <p style="margin: 0 0 5px 0;">${place.vicinity || place.formatted_address}</p>
                                    <p style="margin: 0;">Rating: ${place.rating ? `${place.rating} ⭐` : 'No rating'}</p>
                                    ${place.price_level ? `<p style="margin: 0;">Price: ${
                                        '$'.repeat(place.price_level)
                                    }</p>` : ''}
                                </div>
                            `
                        });

                        marker.addListener('click', () => {
                            infoWindow.open(mapInstance, marker);
                        });
                    }
                });

                setRestaurants(detailedResults);
                setLoading(false);
            } catch (err) {
                console.error('Error loading restaurants:', err);
                setError('Failed to load restaurants');
                setLoading(false);
            }
        };

        initMap();
    }, [searchLocation, navigate]);

    if (!searchLocation) return null;

    return (
        <div className="results-page">
            <div className="results-header">
                <button onClick={() => navigate('/')} className="back-button">
                    <i className="fas fa-arrow-left"></i> Back to Search
                </button>
                <h1>Restaurants near {searchLocation.address}</h1>
            </div>

            <div className="results-container">
                <div id="map" className="map-container"></div>

                <div className="restaurants-list">
                    {loading ? (
                        <div className="loading">Finding restaurants...</div>
                    ) : error ? (
                        <div className="error-message">{error}</div>
                    ) : (
                        restaurants.map(restaurant => (
                            <div 
                                key={restaurant.place_id} 
                                className="restaurant-card"
                                onClick={() => {
                                    if (map && restaurant.geometry) {
                                        map.panTo(restaurant.geometry.location);
                                        map.setZoom(16);
                                    }
                                }}
                            >
                                {restaurant.photos && restaurant.photos[0] && (
                                    <div className="restaurant-image" 
                                         style={{
                                             backgroundImage: `url(${restaurant.photos[0].getUrl()})`,
                                             backgroundSize: 'cover',
                                             backgroundPosition: 'center'
                                         }}
                                    />
                                )}
                                <div className="restaurant-info">
                                    <h3>{restaurant.name}</h3>
                                    <p>{restaurant.vicinity || restaurant.formatted_address}</p>
                                    <div className="restaurant-details">
                                        {restaurant.rating && (
                                            <span className="rating">⭐ {restaurant.rating}</span>
                                        )}
                                        {restaurant.price_level && (
                                            <span className="price">
                                                {'$'.repeat(restaurant.price_level)}
                                            </span>
                                        )}
                                    </div>
                                    {restaurant.opening_hours && (
                                        <p className="opening-status">
                                            {restaurant.opening_hours.isOpen ? 'Open' : 'Closed'}
                                        </p>
                                    )}
                                </div>
                            </div>
                        ))
                    )}
                </div>
            </div>
        </div>
    );
};

export default RestaurantResults;
