import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './MenuPage.css';

const MenuPage = () => {
  const { placeId } = useParams();
  const navigate = useNavigate();
  const [menuData, setMenuData] = useState([]);
  const [restaurant, setRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMenu = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/restaurants/menu/${placeId}`);
        const data = await response.json();

        if (response.ok && data.menu) {
          setMenuData(data.menu.map((item) => ({
            ...item,
            userRating: 0,
            userReview: '',
            reviews: item.reviews || [], // Reviews default to an empty array
            currentPage: 0, // Pagination: Track the current page for each dish
          })));
          setRestaurant(data.restaurant);
        } else {
          setError(data.message || 'Menu not available');
        }
      } catch (error) {
        console.error('Error fetching menu:', error);
        setError('Failed to load menu');
      } finally {
        setLoading(false);
      }
    };

    fetchMenu();
  }, [placeId]);

  const handleRatingChange = (dishIndex, rating) => {
    const updatedMenu = [...menuData];
    updatedMenu[dishIndex].userRating = rating;
    setMenuData(updatedMenu);
  };

  const handleReviewChange = (dishIndex, review) => {
    const updatedMenu = [...menuData];
    updatedMenu[dishIndex].userReview = review;
    setMenuData(updatedMenu);
  };

  const submitReview = async (dishIndex) => {
    const dish = menuData[dishIndex];
    if (dish.userRating === 0) {
      alert('Please provide a rating before submitting');
      return;
    }

    const requestBody = {
      dishName: dish.name,
      placeId: placeId,
      rating: dish.userRating,
      review: dish.userReview || null,
      user: 'CurrentUserName', // Replace this with actual logged-in username
    };

    console.log('Submitting review:', requestBody);

    try {
      const response = await fetch('http://localhost:8000/api/restaurants/reviews', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestBody),
      });

      if (!response.ok) {
        const errorMessage = await response.text();
        console.error('Error response:', errorMessage);
        alert(`Error: ${response.status} - ${errorMessage}`);
        return;
      }

      const data = await response.json();
      alert('Review submitted successfully!');

      const updatedMenu = [...menuData];
      updatedMenu[dishIndex].averageRating = data.averageRating;
      updatedMenu[dishIndex].totalReviews = data.totalReviews;
      updatedMenu[dishIndex].reviews = data.reviews || []; // Update with the latest reviews
      updatedMenu[dishIndex].userReview = '';
      updatedMenu[dishIndex].userRating = 0; // Reset user inputs after submission
      setMenuData(updatedMenu);
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('An unexpected error occurred while submitting the review.');
    }
  };

  const handlePageChange = (dishIndex, direction) => {
    const updatedMenu = [...menuData];
    const totalPages = Math.ceil(updatedMenu[dishIndex].reviews.length / 3);
    const nextPage = updatedMenu[dishIndex].currentPage + direction;

    if (nextPage >= 0 && nextPage < totalPages) {
      updatedMenu[dishIndex].currentPage = nextPage;
      setMenuData(updatedMenu);
    }
  };

  return (
    <div className="menu-page">
      <div className="menu-header">
        <button onClick={() => navigate(-1)} className="back-button">
          <i className="fas fa-arrow-left"></i> Back to Results
        </button>
        <h1>{restaurant?.name || 'Restaurant'} Menu</h1>
      </div>

      <div className="menu-content">
        {loading && <div className="loading">Loading menu...</div>}
        {error && <div className="error-message">{error}</div>}

        {menuData.map((item, index) => (
          <div key={index} className="menu-item">
            <img
              src={item.imageUrl}
              alt={item.name}
              className="menu-item-image"
            />
            <div className="item-info">
              <div className="item-details">  
                <span className="item-name">{item.name}</span>
                <span className="item-price">${item.price}</span>
              </div>
              <div className="item-rating">
                {[1, 2, 3, 4, 5].map((star) => (
                  <i
                    key={star}
                    className={`fas fa-star ${star <= item.userRating ? 'selected' : ''}`}
                    onClick={() => handleRatingChange(index, star)}
                  ></i>
                ))}
                <span className="rating-text">
                  ({item.averageRating?.toFixed(1) || 0} / {item.totalReviews || 0} reviews)
                </span>
              </div>
              <div className="review-section">
                <textarea
                  placeholder="Write a review (optional)..."
                  value={item.userReview}
                  onChange={(e) => handleReviewChange(index, e.target.value)}
                  className="review-textarea"
                ></textarea>
                <button
                  onClick={() => submitReview(index)}
                  className="submit-button"
                >
                  Save Review
                </button>
              </div>
              <div className="past-reviews">
                <h4>Reviews:</h4>
                {item.reviews.length > 0 ? (
                  <>
                    <ul>
                      {item.reviews
                        .slice(item.currentPage * 3, item.currentPage * 3 + 3)
                        .map((review, i) => (
                          <li key={i} className="review-item">
                            <strong>{review.user || 'Anonymous'}:</strong> {review.text} ({review.rating} stars)
                          </li>
                        ))}
                    </ul>
                    <div className="pagination">
                      <button onClick={() => handlePageChange(index, -1)} disabled={item.currentPage === 0}>
                        Previous
                      </button>
                      <button
                        onClick={() => handlePageChange(index, 1)}
                        disabled={(item.currentPage + 1) * 3 >= item.reviews.length}
                      >
                        Next
                      </button>
                    </div>
                  </>
                ) : (
                  <p>No reviews yet.</p>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MenuPage;
